package ejersicios;

public class ejerciciso1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//1) Declara dos variables numéricas (con el valor que desees), muestra por consola la suma, 
		//resta, multiplicación, división y
		//módulo (resto de la división).
		int num1 = 5;
		int num2 = 10;
		int  suma= (num1 + num2);
		int  resta= (num1 - num2);
		int  multiplicacion= (num1 * num2);
		int  divicion= (num1 / num2);
		int modulo = (num1 % num2);
		System.out.println("Suma de numero 1 y numero 2= "+ suma);
		System.out.println("resta de numero 1 y numero 2= "+ resta);
		System.out.println("Multiplicacion de numero 1 y numero 2= "+ multiplicacion);
		System.out.println("Divicion de numero 1 y numero 2= "+ divicion);
		System.out.println("Modulo de numero 1 y numero 2= "+ modulo);
	}

}
